    /**
     *	计算安装费
	 **/
    public function cost($data,$nums){

		if (!empty($nums['经济型']))   $num['1'] = $nums['经济型'];
		if (!empty($nums['舒适型']))   $num['2'] = $nums['舒适型'];
		if (!empty($nums['豪华型']))   $num['3'] = $nums['豪华型'];
		if (!empty($nums['豪华暗装'])) $num['4'] = $nums['豪华暗装'];

		if (!empty($nums['1'])) $num['1'] = $nums['1'];
		if (!empty($nums['2'])) $num['2'] = $nums['2'];

    	for ($i=1; $i <5 ; $i++) { 
    		$cost[$i] = 0;
    		if (!empty($data[$i])) {
    			if (!empty($nums['0']) && $nums['0'] >= '0') $num[$i] = $nums;
    			foreach ($data[$i] as $k => $v) {
					if ($v['goods_lv']=='3') {
						$cost[$i] += $v['money']*$num[$i][$k];
					}
				}
    		}
    	}
		return $cost;
    }

